import React, { useState, useEffect } from "react";
import { Link } from "react-router";
import { motion, AnimatePresence } from "framer-motion";
import { ArrowRight } from "lucide-react";
import Nax from "./Nax";
import Footer from "./Footer";

 

const HathiwalasPage = () => {
  const [filteredItems, setFilteredItems] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 4;

  useEffect(() => {
    setFilteredItems(dummyData);
  }, []);

  const totalPages = Math.ceil(filteredItems.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const currentItems = filteredItems.slice(startIndex, startIndex + itemsPerPage);

  const handlePageClick = (pageNum) => setCurrentPage(pageNum);

  return (
  <div className="min-h-screen bg-[#fdf8f3] text-[#333333]">
      <Nax />

      {/* Hero */}
      <motion.div
        initial={{ opacity: 0, y: 40 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="text-center px-6 py-12 md:py-16 max-w-4xl mx-auto"
      >
        <motion.h1
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="text-3xl md:text-5xl font-serif tracking-widest uppercase font-bold mb-4 text-[#e75b1e]"
        >
          Relive the Forgotten Stories of the Past
        </motion.h1>
        <motion.p
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
          className="text-base md:text-lg text-[#555555]"
        >
          Browse 10 handpicked stories — two side by side, perfectly centered.
        </motion.p>
      </motion.div>

      {/* Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-10 max-w-6xl mx-auto px-4 justify-items-center">
        <AnimatePresence>
          {currentItems.map((item) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, scale: 0.95, y: 50 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.9, y: 30 }}
              transition={{ duration: 0.5 }}
              className="bg-[#fcf5eb] shadow-md rounded-xl overflow-hidden w-full max-w-xl border border-[#e7ddd0]"
            >
              <img
                src={item.image}
                alt={item.title}
                className="w-full h-[320px] object-cover"
              />
              <div className="p-6">
                <h3 className="text-2xl font-bold font-serif text-[#333333] mb-1">
                  {item.title}
                </h3>
                <p className="text-xs uppercase font-semibold text-[#e75b1e] tracking-wider mb-2">
                  {item.category}
                </p>
                <p className="text-[#555555] text-sm mb-4 line-clamp-3">{item.description}</p>
                <Link to={`/article/${item.id}`} state={{ item }}>
                  <button className="border border-[#e75b1e] text-[#e75b1e] px-4 py-2 rounded hover:bg-[#fde7db] transition flex items-center text-sm">
                    Continue Reading
                    <ArrowRight className="ml-2 w-4 h-4" />
                  </button>
                </Link>
              </div>
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {/* Pagination */}
      <div className="flex justify-center items-center gap-2 mt-10 mb-20">
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i}
            onClick={() => handlePageClick(i + 1)}
            className={`w-9 h-9 rounded text-sm font-medium border border-[#e7ddd0] ${
              currentPage === i + 1
                ? "bg-[#e75b1e] text-white"
                : "bg-white text-[#333333] hover:bg-[#fcf5eb]"
            }`}
          >
            {i + 1}
          </button>
        ))}
      </div>

      <Footer />
    </div>
  );
};

export default HathiwalasPage;



